import React, { Component } from 'react';
import './App.scss';
import { Row, Col } from 'reactstrap';
import WeatherDisplay from '../WeatherDisplay';

class App extends Component {
    state = {
        lat: null,
        lon: null
    }

    componentDidMount() {
        const getCoordinates = () => {
            return new Promise(function (resolve, reject) {
                navigator.geolocation.getCurrentPosition(resolve, reject);
            });
        }
        const getLocation = async () => {
            const position = await this.getCoordinates();
            console.log(position);
        }
        getLocation();
        /*
        const getLocation = () => {
            if ('geolocation' in navigator) {
                navigator.geolocation.getCurrentPosition((position) => {
                    this.setState({ lat: position.coords.latitude, lon: position.coords.longitude });
                });
            } else {
                alert("Geolocation Failed. This usually means access was denied or HTTPS isn't running.");
            }
        }
        getLocation();*/
    }

    getWeather = async () => {
        const response = (await fetch(`https://cors-anywhere.herokuapp.com/http://api.openweathermap.org/data/2.5/weather?q=London&appid=fdca481f9e858844aede00aa6a7749c2`).catch(err => console.log(err)));
        const weather = response.json()
    }

    handleClick = () => {
        console.log('button clicked');
    }

    render() {

        return (
            <div>
                <Row>
                    <Col>
                        <WeatherDisplay
                            lat={this.state.lat}
                            lon={this.state.lon}
                            handleClick={this.handleClick}
                        />
                    </Col>
                </Row>
            </div>
        )
    }
};

export default App;
